import React from 'react';
import { motion } from 'framer-motion';
import logger from '../../utils/logger';
import animationSystem from '../../utils/animations';

const AnimatedButton = ({ 
  children, 
  variant = 'primary', 
  size = 'md', 
  disabled = false, 
  loading = false,
  animationType = 'buttonHover',
  disableMotion = false,
  onClick,
  className = '',
  type = 'button',
  ...props 
}) => {

  const handleClick = (e) => {
    logger.ui('CLICK', 'AnimatedButton', { variant, size, disabled, loading, animationType }, 'UI_ANIMATED_BUTTON');
    if (onClick && !disabled && !loading) {
      const startTime = performance.now();
      onClick(e);
      animationSystem.logAnimationPerformance('ButtonClick', startTime);
    }
  };

  // Base styles
  const baseStyles = [
    'inline-flex items-center justify-center font-medium rounded-lg',
    'transition-all duration-200 focus:outline-none focus:ring-2 focus:ring-offset-2',
    'disabled:opacity-50 disabled:cursor-not-allowed',
    // Global highlighter: subtle default depth + yellow glow on hover (light/dark)
    'shadow-medium hover:shadow-strong',
    'hover:shadow-yellow-400/30 dark:hover:shadow-yellow-300/30',
    'hover:ring-2 hover:ring-yellow-400/50 dark:hover:ring-yellow-300/50'
  ].join(' ');

  // Variant styles
  const variants = {
    primary: 'bg-primary-600 hover:bg-primary-700 text-white focus:ring-primary-500',
    secondary: 'bg-secondary-100 hover:bg-secondary-200 text-secondary-900 focus:ring-secondary-500 border border-secondary-200',
    outline: 'border-2 border-primary-600 text-primary-600 hover:bg-primary-600 hover:text-white focus:ring-primary-500',
    ghost: 'text-slate-600 hover:bg-slate-100 dark:text-slate-400 dark:hover:bg-slate-800 focus:ring-slate-500',
    danger: 'bg-red-600 hover:bg-red-700 text-white focus:ring-red-500',
    success: 'bg-green-600 hover:bg-green-700 text-white focus:ring-green-500'
  };

  // Size styles
  const sizes = {
    sm: 'px-3 py-1.5 text-sm',
    md: 'px-4 py-2 text-sm',
    lg: 'px-6 py-3 text-base',
    xl: 'px-8 py-4 text-lg'
  };

  const buttonClasses = `${baseStyles} ${variants[variant]} ${sizes[size]} ${className}`;

  // Get animation configuration (or disable entirely)
  const animation = disableMotion ? {} : animationSystem.getAnimation('microInteractions', animationType);
  const reducedMotionAnimation = disableMotion ? {} : animationSystem.getReducedMotionAnimation(animation);

  logger.debug(`AnimatedButton rendered with variant: ${variant}, size: ${size}, animation: ${animationType}`, 'UI_ANIMATED_BUTTON');

  if (disableMotion) {
    return (
      <button
        className={buttonClasses}
        disabled={disabled || loading}
        onClick={handleClick}
        type={type}
        {...props}
      >
        {children}
      </button>
    );
  }

  return (
    <motion.button
      className={buttonClasses}
      disabled={disabled || loading}
      onClick={handleClick}
      type={type}
      {...reducedMotionAnimation}
      onAnimationStart={() => logger.ui('ANIMATION_START', 'AnimatedButton', { animationType }, 'UI_ANIMATED_BUTTON')}
      onAnimationComplete={() => logger.ui('ANIMATION_COMPLETE', 'AnimatedButton', { animationType }, 'UI_ANIMATED_BUTTON')}
      {...props}
    >
      {children}
    </motion.button>
  );
};

export default AnimatedButton;
