import React, { useEffect } from 'react';
import { Link } from 'react-router-dom';
import { motion } from 'framer-motion';
import { useNavigateWithLoading } from '../../hooks/useNavigateWithLoading';
import { useLoading } from '../../contexts/LoadingContext';
import { 
  Heart, 
  Users, 
  Clock, 
  Award, 
  ArrowRight,
  Phone,
  MapPin,
  Shield
} from 'lucide-react';

import logger from '../../utils/logger';
import { 
  AnimatedButton, 
  AnimatedCard, 
  AnimatedList, 
  AnimatedListItem,
  FadeInWhenVisible 
} from '../../components/ui';

const HomePage = () => {
  useEffect(() => {
    logger.componentMount('HomePage');
    logger.startTimer('HomePage Render');

    return () => {
      logger.componentUnmount('HomePage');
      logger.endTimer('HomePage Render');
    };
  }, []);

  // Animation variants
  const containerVariants = {
    hidden: { opacity: 0 },
    visible: {
      opacity: 1,
      transition: {
        staggerChildren: 0.2
      }
    }
  };

  const itemVariants = {
    hidden: { opacity: 0, y: 20 },
    visible: {
      opacity: 1,
      y: 0,
      transition: {
        duration: 0.6,
        ease: 'easeOut'
      }
    }
  };

  const stats = [
    { label: 'Registered Donors', value: '50,000+', icon: Users },
    { label: 'Lives Saved', value: '25,000+', icon: Heart },
    { label: 'Response Time', value: '< 2 min', icon: Clock },
    { label: 'Success Rate', value: '95%', icon: Award },
  ];

  const features = [
    {
      icon: Phone,
      title: 'WhatsApp Integration',
      description: 'Receive and respond to blood requests directly through WhatsApp with one-click responses.'
    },
    {
      icon: MapPin,
      title: 'Smart Matching',
      description: 'AI-powered algorithm matches donors based on location, blood type, and availability.'
    },
    {
      icon: Shield,
      title: 'Secure & Private',
      description: 'Your medical data is encrypted and protected with enterprise-grade security.'
    }
  ];

  return (
    <div className="min-h-screen">
      {/* Enhanced Hero Section */}
      <section className="relative bg-gradient-to-br from-red-600 via-red-700 to-red-800 text-white overflow-hidden min-h-screen">
        {/* Animated Background Elements */}
        <div className="absolute inset-0">
          {/* Floating Hearts */}
          <div className="absolute top-20 left-10 animate-float">
            <Heart className="h-8 w-8 text-red-300 opacity-30 fill-current" />
          </div>
          <div className="absolute top-40 right-20 animate-float-delayed">
            <Heart className="h-6 w-6 text-red-300 opacity-40 fill-current" />
          </div>
          <div className="absolute bottom-32 left-1/4 animate-bounce-gentle">
            <Heart className="h-10 w-10 text-red-300 opacity-20 fill-current" />
          </div>

          {/* Animated Medical Icons */}
          <motion.div
            animate={{ scale: [1, 1.1, 1], opacity: [0.2, 0.4, 0.2] }}
            transition={{ duration: 4, repeat: Infinity, ease: 'easeInOut' }}
            className="absolute top-60 right-10"
          >
            <Users className="h-12 w-12 text-yellow-300" />
          </motion.div>
          <motion.div
            animate={{ scale: [1, 1.2, 1], opacity: [0.15, 0.35, 0.15] }}
            transition={{ duration: 5, repeat: Infinity, ease: 'easeInOut', delay: 1 }}
            className="absolute bottom-40 right-1/3"
          >
            <Clock className="h-10 w-10 text-yellow-300" />
          </motion.div>

          {/* Geometric Shapes */}
          <div className="absolute top-60 right-10 w-20 h-20 border border-white opacity-10 rounded-full animate-spin-slow"></div>
          <div className="absolute bottom-40 right-1/3 w-16 h-16 border border-white opacity-15 rotate-45 animate-pulse"></div>

          {/* Gradient Overlays */}
          <div className="absolute inset-0 bg-gradient-to-r from-red-600/30 to-transparent"></div>
          <div className="absolute inset-0 bg-gradient-to-t from-black/20 to-transparent"></div>

          {/* Particle Effect */}
          <div className="absolute inset-0 opacity-20" style={{
            backgroundImage: `url("data:image/svg+xml,%3Csvg width='60' height='60' viewBox='0 0 60 60' xmlns='http://www.w3.org/2000/svg'%3E%3Cg fill='none' fill-rule='evenodd'%3E%3Cg fill='%23ffffff' fill-opacity='0.05'%3E%3Ccircle cx='30' cy='30' r='2'/%3E%3C/g%3E%3C/g%3E%3C/svg%3E")`
          }}></div>
        </div>

        <div className="relative max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-24 md:py-32">
          <div className="grid grid-cols-1 lg:grid-cols-2 gap-12 items-center">
            {/* Left Content */}
            <motion.div
              variants={containerVariants}
              initial="hidden"
              animate="visible"
              className="space-y-8"
            >
              <div className="space-y-6">
                <motion.div
                  variants={itemVariants}
                  className="flex items-center space-x-2 text-yellow-300 font-semibold"
                >
                  <Award className="h-5 w-5" />
                  <span className="text-sm uppercase tracking-wide">Trusted Platform</span>
                </motion.div>

                <motion.h1
                  variants={itemVariants}
                  className="text-4xl md:text-6xl lg:text-7xl font-bold leading-tight"
                >
                  <span className="block">Be the</span>
                  <span className="block gradient-text animate-glow">
                    Lifeline.
                  </span>
                  <span className="block">
                    <span className="text-red-200">Donate Blood.</span>
                  </span>
                  <span className="block text-2xl md:text-4xl lg:text-5xl text-red-200 font-semibold">
                    Save Lives.
                  </span>
                </motion.h1>

                <motion.p
                  variants={itemVariants}
                  className="text-xl md:text-2xl text-red-100 max-w-2xl leading-relaxed"
                >
                  Every drop counts. Join our community of 
                  <span className="text-yellow-300 font-semibold"> 50,000+ heroes </span>
                  and make a difference in someone's life today.
                </motion.p>
              </div>

              {/* Enhanced CTA Buttons */}
              <motion.div
                variants={itemVariants}
                className="flex flex-col sm:flex-row gap-4"
              >
                <Link to="/emergency">
                  <motion.div
                    whileHover={{ scale: 1.05 }}
                    whileTap={{ scale: 0.95 }}
                    className="group bg-yellow-400 hover:bg-yellow-500 text-red-900 px-8 py-4 rounded-xl font-bold text-lg transition-all duration-300 flex items-center justify-center space-x-2 shadow-2xl hover:shadow-yellow-400/25 animate-glow"
                  >
                    <Heart className="h-6 w-6 group-hover:animate-heartbeat" />
                    <span>Need Blood Now</span>
                    <ArrowRight className="h-5 w-5 group-hover:translate-x-1 transition-transform" />
                  </motion.div>
                </Link>
                <Link to="/register">
                  <motion.div
                    whileHover={{ scale: 1.05 }}
                    whileTap={{ scale: 0.95 }}
                    className="group border-2 border-white text-white hover:bg-white hover:text-red-600 px-8 py-4 rounded-xl font-semibold text-lg transition-all duration-300 flex items-center justify-center space-x-2 glass-effect"
                  >
                    <Users className="h-5 w-5" />
                    <span>Become a Donor</span>
                  </motion.div>
                </Link>
              </motion.div>

              {/* Quick Access Links */}
              <motion.div
                variants={itemVariants}
                className="flex flex-wrap gap-3 pt-4"
              >
                <Link
                  to="/auth/login"
                  className="glass-effect text-white px-4 py-2 rounded-lg text-sm font-medium transition-all duration-300 hover:scale-105 hover:bg-red-500/20"
                >
                  Donor Login
                </Link>
                <Link
                  to="/emergency"
                  className="glass-effect text-white px-4 py-2 rounded-lg text-sm font-medium transition-all duration-300 hover:scale-105 hover:bg-blue-500/20"
                >
                  Emergency Request
                </Link>
                <Link
                  to="/about"
                  className="glass-effect text-white px-4 py-2 rounded-lg text-sm font-medium transition-all duration-300 hover:scale-105 hover:bg-gray-500/20"
                >
                  Learn More
                </Link>
              </motion.div>

              {/* Trust Indicators */}
              <motion.div
                variants={itemVariants}
                className="flex items-center space-x-6 pt-6"
              >
                <div className="flex items-center space-x-2">
                  <Shield className="h-5 w-5 text-green-400" />
                  <span className="text-sm text-red-100">Secure Platform</span>
                </div>
                <div className="flex items-center space-x-2">
                  <Clock className="h-5 w-5 text-blue-400" />
                  <span className="text-sm text-red-100">24/7 Support</span>
                </div>
                <div className="flex items-center space-x-2">
                  <Award className="h-5 w-5 text-yellow-400" />
                  <span className="text-sm text-red-100">95% Success Rate</span>
                </div>
              </motion.div>
            </motion.div>

            {/* Right Content - Enhanced Visual */}
            <motion.div
              initial={{ opacity: 0, scale: 0.8 }}
              animate={{ opacity: 1, scale: 1 }}
              transition={{ duration: 1, delay: 0.5 }}
              className="relative flex justify-center items-center"
            >
              <div className="relative w-80 h-80 md:w-96 md:h-96">
                {/* Central Heart Animation */}
                <div className="absolute inset-0 flex items-center justify-center">
                  <div className="animate-pulse-slow">
                    <Heart className="h-32 w-32 md:h-40 md:w-40 text-yellow-300 fill-current" />
                  </div>
                </div>

                {/* Orbiting Icons */}
                <div className="absolute inset-0 animate-spin-slow">
                  <div className="absolute top-4 left-1/2 transform -translate-x-1/2 animate-bounce-gentle">
                    <Users className="h-8 w-8 text-yellow-300" />
                  </div>
                  <div className="absolute right-4 top-1/2 transform -translate-y-1/2 animate-bounce-gentle">
                    <Clock className="h-8 w-8 text-yellow-300" />
                  </div>
                  <div className="absolute bottom-4 left-1/2 transform -translate-x-1/2 animate-bounce-gentle">
                    <Award className="h-8 w-8 text-yellow-300" />
                  </div>
                  <div className="absolute left-4 top-1/2 transform -translate-y-1/2 animate-bounce-gentle">
                    <Shield className="h-8 w-8 text-yellow-300" />
                  </div>
                </div>

                {/* Pulse Rings */}
                <motion.div
                  animate={{ scale: [1, 2], opacity: [0.3, 0] }}
                  transition={{ duration: 2, repeat: Infinity, ease: 'easeOut' }}
                  className="absolute inset-0 border-2 border-yellow-300 rounded-full"
                ></motion.div>
                <motion.div
                  animate={{ scale: [1, 2], opacity: [0.3, 0] }}
                  transition={{ duration: 2, repeat: Infinity, ease: 'easeOut', delay: 1 }}
                  className="absolute inset-0 border-2 border-red-300 rounded-full"
                ></motion.div>
              </div>
            </motion.div>
          </div>
        </div>

        {/* Bottom Wave */}
        <div className="absolute bottom-0 left-0 right-0">
          <svg viewBox="0 0 1440 120" fill="none" xmlns="http://www.w3.org/2000/svg">
            <path
              d="M0 120L60 110C120 100 240 80 360 70C480 60 600 60 720 65C840 70 960 80 1080 85C1200 90 1320 90 1380 90L1440 90V120H1380C1320 120 1200 120 1080 120C960 120 840 120 720 120C600 120 480 120 360 120C240 120 120 120 60 120H0Z"
              fill="white"
            />
          </svg>
        </div>
      </section>

      {/* Stats Section */}
      <section className="py-16 bg-white dark:bg-slate-900">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <FadeInWhenVisible>
            <AnimatedList className="grid grid-cols-2 lg:grid-cols-4 gap-8">
              {stats.map((stat, index) => {
                const Icon = stat.icon;
                return (
                  <AnimatedListItem
                    key={stat.label}
                    className="text-center"
                  >
                    <div className="inline-flex items-center justify-center w-16 h-16 bg-primary-100 dark:bg-primary-900/20 rounded-full mb-4">
                      <Icon className="h-8 w-8 text-primary-600 dark:text-primary-400" />
                    </div>
                    <div className="text-3xl font-bold text-slate-900 dark:text-white mb-2">
                      {stat.value}
                    </div>
                    <div className="text-slate-600 dark:text-slate-400">
                      {stat.label}
                    </div>
                  </AnimatedListItem>
                );
              })}
            </AnimatedList>
          </FadeInWhenVisible>
        </div>
      </section>

      {/* Features Section */}
      <section className="py-20 bg-slate-50 dark:bg-slate-800">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <motion.div
            variants={containerVariants}
            initial="hidden"
            whileInView="visible"
            viewport={{ once: true }}
            className="text-center mb-16"
          >
            <motion.h2
              variants={itemVariants}
              className="text-3xl md:text-4xl font-bold text-slate-900 dark:text-white mb-4"
            >
              Why Choose Call For Blood?
            </motion.h2>
            <motion.p
              variants={itemVariants}
              className="text-xl text-slate-600 dark:text-slate-400 max-w-3xl mx-auto"
            >
              Our platform combines cutting-edge technology with compassionate care 
              to create the most efficient blood donation ecosystem.
            </motion.p>
          </motion.div>

          <FadeInWhenVisible>
            <AnimatedList className="grid md:grid-cols-3 gap-8">
              {features.map((feature, index) => {
                const Icon = feature.icon;
                return (
                  <AnimatedListItem key={feature.title}>
                    <AnimatedCard 
                      hover={true}
                      className="text-center"
                      onClick={() => {
                        logger.ui('CLICK', 'FeatureCard', { feature: feature.title }, 'HOMEPAGE_FEATURES');
                      }}
                    >
                      <div className="inline-flex items-center justify-center w-16 h-16 bg-primary-100 dark:bg-primary-900/20 rounded-full mb-6">
                        <Icon className="h-8 w-8 text-primary-600 dark:text-primary-400" />
                      </div>
                      <h3 className="text-xl font-semibold text-slate-900 dark:text-white mb-4">
                        {feature.title}
                      </h3>
                      <p className="text-slate-600 dark:text-slate-400 leading-relaxed">
                        {feature.description}
                      </p>
                    </AnimatedCard>
                  </AnimatedListItem>
                );
              })}
            </AnimatedList>
          </FadeInWhenVisible>
        </div>
      </section>

      {/* CTA Section */}
      <section className="py-20 bg-primary-600">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 text-center">
          <motion.div
            variants={containerVariants}
            initial="hidden"
            whileInView="visible"
            viewport={{ once: true }}
          >
            <motion.h2
              variants={itemVariants}
