import React, { useState, useEffect } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import { X, AlertTriangle, Clock, MapPin, Heart, Phone, User } from 'lucide-react';

const EmergencyToast = ({ 
  isVisible, 
  onClose, 
  patientData,
  autoClose = false,
  autoCloseDelay = 10000 
}) => {
  const [timeLeft, setTimeLeft] = useState(autoCloseDelay / 1000);

  useEffect(() => {
    if (!isVisible || !autoClose) return;

    const timer = setInterval(() => {
      setTimeLeft(prev => {
        if (prev <= 1) {
          onClose();
          return 0;
        }
        return prev - 1;
      });
    }, 1000);

    return () => clearInterval(timer);
  }, [isVisible, autoClose, onClose]);

  useEffect(() => {
    if (isVisible && autoClose) {
      setTimeLeft(autoCloseDelay / 1000);
    }
  }, [isVisible, autoClose, autoCloseDelay]);

  const toastVariants = {
    hidden: { 
      y: 100,
      opacity: 0,
      scale: 0.95
    },
    visible: { 
      y: 0,
      opacity: 1,
      scale: 1,
      transition: {
        duration: 0.5,
        ease: "easeOut"
      }
    },
    exit: { 
      y: 100,
      opacity: 0,
      scale: 0.95,
      transition: {
        duration: 0.3,
        ease: "easeIn"
      }
    }
  };

  const pulseVariants = {
    pulse: {
      scale: [1, 1.05, 1],
      transition: {
        duration: 1.5,
        repeat: Infinity,
        ease: "easeInOut"
      }
    }
  };

  if (!patientData) return null;

  return (
    <AnimatePresence>
      {isVisible && (
        <motion.div
          variants={toastVariants}
          initial="hidden"
          animate="visible"
          exit="exit"
          className="fixed bottom-4 left-4 right-4 md:left-6 md:right-6 z-50 max-w-2xl mx-auto"
        >
          <div className="relative bg-gradient-to-r from-red-600 via-red-700 to-red-800 rounded-2xl shadow-2xl border-2 border-red-400/30 overflow-hidden">
            {/* Background Pattern */}
            <div className="absolute inset-0 opacity-10">
              <div className="absolute inset-0" style={{
                backgroundImage: `url("data:image/svg+xml,%3Csvg width='60' height='60' viewBox='0 0 60 60' xmlns='http://www.w3.org/2000/svg'%3E%3Cg fill='none' fill-rule='evenodd'%3E%3Cg fill='%23ffffff' fill-opacity='0.1'%3E%3Ccircle cx='30' cy='30' r='2'/%3E%3C/g%3E%3C/g%3E%3C/svg%3E")`
              }}></div>
            </div>

            {/* Animated Border */}
            <div className="absolute inset-0 rounded-2xl">
              <motion.div
                animate={{ rotate: 360 }}
                transition={{ duration: 8, repeat: Infinity, ease: "linear" }}
                className="absolute -inset-1 bg-gradient-to-r from-yellow-400 via-red-500 to-yellow-400 rounded-2xl opacity-30 blur-sm"
              ></motion.div>
            </div>

            {/* Content */}
            <div className="relative p-6">
              {/* Header */}
              <div className="flex items-center justify-between mb-4">
                <div className="flex items-center space-x-3">
                  <motion.div
                    variants={pulseVariants}
                    animate="pulse"
                    className="flex items-center justify-center w-12 h-12 bg-yellow-400 rounded-full shadow-lg"
                  >
                    <AlertTriangle className="h-6 w-6 text-red-900" />
                  </motion.div>
                  <div>
                    <h3 className="text-xl font-bold text-white">Emergency Blood Request</h3>
                    <p className="text-red-100 text-sm">Urgent donor needed</p>
                  </div>
                </div>
                
                {/* Close Button */}
                <motion.button
                  whileHover={{ scale: 1.1 }}
                  whileTap={{ scale: 0.9 }}
                  onClick={onClose}
                  className="flex items-center justify-center w-8 h-8 bg-white/20 hover:bg-white/30 rounded-full transition-colors duration-200"
                >
                  <X className="h-5 w-5 text-white" />
                </motion.button>
              </div>

              {/* Patient Details */}
              <div className="grid grid-cols-1 md:grid-cols-2 gap-4 mb-4">
                <div className="space-y-3">
                  <div className="flex items-center space-x-3">
                    <User className="h-5 w-5 text-yellow-300" />
                    <div>
                      <p className="text-red-100 text-sm">Patient Name</p>
                      <p className="text-white font-semibold">{patientData.name || 'Anonymous'}</p>
                    </div>
                  </div>
                  
                  <div className="flex items-center space-x-3">
                    <Heart className="h-5 w-5 text-yellow-300" />
                    <div>
                      <p className="text-red-100 text-sm">Blood Type</p>
                      <p className="text-white font-bold text-lg">{patientData.bloodType || 'O+'}</p>
                    </div>
                  </div>
                </div>

                <div className="space-y-3">
                  <div className="flex items-center space-x-3">
                    <MapPin className="h-5 w-5 text-yellow-300" />
                    <div>
                      <p className="text-red-100 text-sm">Location</p>
                      <p className="text-white font-semibold">{patientData.location || 'City Hospital'}</p>
                    </div>
                  </div>
                  
                  <div className="flex items-center space-x-3">
                    <Clock className="h-5 w-5 text-yellow-300" />
                    <div>
                      <p className="text-red-100 text-sm">Time Needed</p>
                      <p className="text-white font-semibold">{patientData.timeNeeded || 'ASAP'}</p>
                    </div>
                  </div>
                </div>
              </div>

              {/* Additional Info */}
              {patientData.condition && (
                <div className="mb-4 p-3 bg-black/20 rounded-lg">
                  <p className="text-red-100 text-sm mb-1">Medical Condition</p>
                  <p className="text-white font-medium">{patientData.condition}</p>
                </div>
              )}

              {/* Action Buttons */}
              <div className="flex flex-col sm:flex-row gap-3">
                <motion.button
                  whileHover={{ scale: 1.02 }}
                  whileTap={{ scale: 0.98 }}
                  className="flex-1 bg-yellow-400 hover:bg-yellow-500 text-red-900 px-6 py-3 rounded-lg font-bold text-center transition-colors duration-200 flex items-center justify-center space-x-2"
                >
                  <Heart className="h-5 w-5" />
                  <span>I Can Donate</span>
                </motion.button>
                
                <motion.button
                  whileHover={{ scale: 1.02 }}
                  whileTap={{ scale: 0.98 }}
                  className="flex-1 bg-white/20 hover:bg-white/30 text-white px-6 py-3 rounded-lg font-semibold text-center transition-colors duration-200 flex items-center justify-center space-x-2"
                >
                  <Phone className="h-5 w-5" />
                  <span>Call Hospital</span>
                </motion.button>
              </div>

              {/* Auto-close Timer */}
              {autoClose && timeLeft > 0 && (
                <div className="mt-4">
                  <div className="flex items-center justify-between text-sm text-red-100 mb-2">
                    <span>Auto-closing in {timeLeft}s</span>
                    <span>{Math.round((timeLeft / (autoCloseDelay / 1000)) * 100)}%</span>
                  </div>
                  <div className="w-full bg-red-800/50 rounded-full h-1">
                    <motion.div
                      initial={{ width: "100%" }}
                      animate={{ width: "0%" }}
                      transition={{ duration: autoCloseDelay / 1000, ease: "linear" }}
                      className="h-1 bg-yellow-400 rounded-full"
                    ></motion.div>
                  </div>
                </div>
              )}
            </div>

            {/* Floating Hearts Animation */}
            <div className="absolute top-2 right-2">
              <motion.div
                animate={{ y: [0, -10, 0] }}
                transition={{ duration: 2, repeat: Infinity, ease: "easeInOut" }}
              >
                <Heart className="h-4 w-4 text-yellow-300 opacity-50 fill-current" />
              </motion.div>
            </div>
          </div>
        </motion.div>
      )}
    </AnimatePresence>
  );
};

export default EmergencyToast;
