import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import { motion } from 'framer-motion';
import { useNavigateWithLoading } from '../../hooks/useNavigateWithLoading';
import { useLoading } from '../../contexts/LoadingContext';
import { useToast } from '../../contexts/ToastContext';
import { 
  Heart, 
  Users, 
  Clock, 
  Award, 
  ArrowRight,
  Phone,
  MapPin,
  Shield
} from 'lucide-react';

import logger from '../../utils/logger';
import { 
  AnimatedButton, 
  AnimatedCard, 
  AnimatedList, 
  AnimatedListItem,
  FadeInWhenVisible,
  Modal 
} from '../../components/ui';

const HomePage = () => {
  // Festival banner modal state
  const [isBannerOpen, setIsBannerOpen] = useState(false);
  const [dontShowToday, setDontShowToday] = useState(false);

  const navigateWithLoading = useNavigateWithLoading();
  const { showLoading } = useLoading();
  const { showEmergencyAlert, showSuccessToast, showErrorToast } = useToast();

  useEffect(() => {
    // Open festival/special-occasion banner once per day unless dismissed
    const todayKey = `festival-banner-${new Date().toISOString().slice(0,10)}`;
    const dismissed = localStorage.getItem(todayKey) === 'dismissed';
    if (!dismissed) {
      setIsBannerOpen(true);
    }

    logger.componentMount('HomePage');
    logger.startTimer('HomePage Render');

    // Simulate an emergency toast after 3 seconds (for demo purposes)
    const timer = setTimeout(() => {
      showEmergencyAlert({
        name: 'Sarah Johnson',
        bloodType: 'O-',
        location: 'City General Hospital, Room 204',
        timeNeeded: 'ASAP - Critical',
        condition: 'Emergency surgery - major blood loss'
      });
    }, 3000);

    return () => {
      logger.componentUnmount('HomePage');
      logger.endTimer('HomePage Render');
      clearTimeout(timer);
    };
  }, [showEmergencyAlert]);

  const handleButtonClick = (path, message) => {
    logger.ui('CLICK', 'NavigationButton', { path }, 'HOMEPAGE_NAVIGATION');
    navigateWithLoading(path, { message });
  };

  const handleEmergencyRequest = () => {
    showEmergencyAlert({
      name: 'John Smith',
      bloodType: 'A+',
      location: 'Metro Hospital, Emergency Ward',
      timeNeeded: 'Within 1 hour',
      condition: 'Accident victim - urgent transfusion needed'
    });
    showSuccessToast('Emergency request has been sent to nearby donors!');
  };

  // Eligibility checker state (for new hero)
  const [form, setForm] = useState({
    age: '',
    weight: '',
    lastDonationDate: '',
    fever: false,
    medication: false,
    tattoo: false
  });
  const [eligibility, setEligibility] = useState(null);
  const [errors, setErrors] = useState({});

  const onChange = (e) => {
    const { name, value, type, checked } = e.target;
    setForm((prev) => ({ ...prev, [name]: type === 'checkbox' ? checked : value }));
  };

  const daysSince = (dateStr) => {
    if (!dateStr) return Infinity;
    const ms = Date.now() - new Date(dateStr).getTime();
    return Math.floor(ms / (1000 * 60 * 60 * 24));
  };

  const validateEligibilityInputs = () => {
    const e = {};
    if (!form.age) e.age = 'Required';
    if (!form.weight) e.weight = 'Required';
    if (!form.lastDonationDate) e.lastDonationDate = 'Required';
    setErrors(e);
    return Object.keys(e).length === 0;
  };

  const checkEligibility = () => {
    if (!validateEligibilityInputs()) return;
    const age = Number(form.age);
    const weight = Number(form.weight);
    const sinceDays = daysSince(form.lastDonationDate);

    const reasons = [];
    if (isNaN(age) || age < 18 || age > 60) reasons.push('Age must be between 18 and 60');
    if (isNaN(weight) || weight < 50) reasons.push('Weight must be at least 50 kg');
    if (sinceDays < 90) reasons.push('Last donation should be at least 90 days ago');
    if (form.fever) reasons.push('Fever in the last 14 days');
    if (form.medication) reasons.push('On antibiotics or contraindicated meds');
    if (form.tattoo) reasons.push('Tattoo/Piercing in the last 6 months');

    setEligibility(reasons.length ? { ok: false, reasons } : { ok: true });
  };

  const closeBanner = () => {
    if (dontShowToday) {
      const todayKey = `festival-banner-${new Date().toISOString().slice(0,10)}`;
      localStorage.setItem(todayKey, 'dismissed');
    }
    setIsBannerOpen(false);
  };

  // Animation variants
  const containerVariants = {
    hidden: { opacity: 0 },
    visible: {
      opacity: 1,
      transition: {
        staggerChildren: 0.2
      }
    }
  };

  const itemVariants = {
    hidden: { opacity: 0, y: 20 },
    visible: {
      opacity: 1,
      y: 0,
      transition: {
        duration: 0.6,
        ease: 'easeOut'
      }
    }
  };

  const stats = [
